function aliasKey(value: string) {
  return value.trim().toLowerCase().replace(/[\s_-]+/g, '');
}

const provinceAliasMap = new Map<string, string>();

chinaProvinceShapes.forEach((shape) => {
  provinceAliasMap.set(aliasKey(shape.id), shape.id);
  shape.aliases.forEach((alias) => {
    provinceAliasMap.set(aliasKey(alias), shape.id);
  });
});

export function normalizeProvinceId(value: string) {
  const key = aliasKey(value);
  return provinceAliasMap.get(key) || key;
}
